<script>
  import {onDestroy, onMount} from 'svelte';
  import {cubicInOut} from 'svelte/easing';
  import {LINKS} from '../utils';
  import {closeIconState, menuState} from '../stores';

  export let wrapRef;
  export let hasAnother;

  function onClickDocument(e) {
    if (wrapRef && !wrapRef.contains(e.target)) {
      closeIconState.remove();
      menuState.close();
    }
  }

  function onClickButton() {
    if (!hasAnother) return;
    menuState.close();
  }

  function open(node) {
    const opacity = +getComputedStyle(node).opacity;

    return {
      duration: 400,
      easing: cubicInOut,
      css: t => {
        return `opacity: ${t * opacity};
        transform: scale(${(t * 0.1) + 0.9});
        transform-origin: bottom right;
        `;
      },
    };
  }

  onMount(() => {
    document.addEventListener('click', onClickDocument);
  });

  onDestroy(() => {
    document.removeEventListener('click', onClickDocument);
  });
</script>

<div class='menu__inner' transition:open>
  <div class='menu__inner-list'>
    {#each LINKS as {name, icon, id, link}(id)}
      <div class='menu__inner-item'>
        <svg>
          <use href='#{icon}'></use>
        </svg>
        <a href={link} target='_blank'>
          {name}
        </a>
      </div>
    {/each}

    {#if hasAnother}
      <button class='menu__inner-another' on:click={onClickButton}>
        <svg>
          <use href='#another'></use>
        </svg>
        Chat
      </button>
    {/if}

  </div>
  <div class='menu__inner-demo'>
    <span>
      <a href='/link'
         target='_blank'>Link</a>
    </span>
  </div>
</div>

